mapper.update:
UPDATE OTREEUSER
SET name = #{name}, password = #{password}, email = #{email},
profileSrc = #{profileSrc}
WHERE userId = #{userId}

mapper.select:
SELECT userId, name, password, email, profileSrc
FROM OTREEUSER
WHERE email = #{email}

SELECT userId, name, password, email, profileSrc
FROM OTREEUSER
WHERE userId = #{userId}
